var uuidType = java.util.UUID;
var HashMapType = java.util.HashMap;
var longType = java.lang.Long;

var albumID = executor.getExecutionProperties().getProperty("albumId");
var eventDetailsAlbum = executor.getContextAlbum("SimpleCL_EventDetailsAlbum").get(albumID.toString());
executor.logger.info("HandleAAISuccessResponseTask extracted context album - " + albumID.toString() + "::" + eventDetailsAlbum);

var commonEventHeader = eventDetailsAlbum.get("commonEventHeader");
var otherFieldsMap = eventDetailsAlbum.get("otherFields").get("hashMap");

var targetEntityName = executor.inFields.get("pnf-name")
var logMessage = "";
var existingHostname = executor.inFields.get("pnf-name2");
var hostnameInVesEvent = otherFieldsMap.get("hostname");

var albumSchemaHelper = executor.getContextAlbum("SimpleCL_EventDetailsAlbum").getSchemaHelper();
var target = albumSchemaHelper.createNewSubInstance("target");
target.put("targetType",  "PNF")
var entityInfo = albumSchemaHelper.createNewSubInstance("entityInfo");
entityInfo.put("name",targetEntityName);
target.put("entityInfo", entityInfo);
eventDetailsAlbum.put("target", target);

var returnVal = true;
if ((String(hostnameInVesEvent) != "UNDEFINED") && (String(existingHostname) != String(hostnameInVesEvent))) {
    logMessage = "Hostname available in AAI does not match with the hostname received in the VES Event.";
    returnVal = false
    var logEventFields = populateLogEventFields(commonEventHeader.get("eventId"), otherFieldsMap.get("pnfName"), "PNF", "FINAL_FAILURE", "Failed to process VES event. Reason: " + logMessage)
    executor.addFieldsToOutput(logEventFields);
} else {
    logMessage = "Received response from AAI successfully. Hostname in AAI matches with the one in Ves event. Going to make the update-config request to CDS.";

    var commonHeader = executor.subject.getOutFieldSchemaHelper("SimpleCL_CDSConfigModifyRequestEvent", "commonHeader").createNewInstance();
    commonHeader.put("subRequestId", albumID.toString());
    commonHeader.put("requestId", commonEventHeader.get("eventId"));
    commonHeader.put("originatorId", "POLICY");

    var blueprintName = "controlloop-hostname-update";
    var blueprintVersion = "1.0.4";
    var newHostname = "host" + Math.random().toString(36).substring(7);
    eventDetailsAlbum.put("generatedHostname", newHostname);

    var actionIdentifiers = executor.subject.getOutFieldSchemaHelper("SimpleCL_CDSConfigModifyRequestEvent", "actionIdentifiers").createNewInstance();
    actionIdentifiers.put("mode", "sync");
    actionIdentifiers.put("blueprintName", blueprintName);
    actionIdentifiers.put("blueprintVersion", blueprintVersion);
    actionIdentifiers.put("actionName", "update-config");

    var payloadEntry = executor.subject.getOutFieldSchemaHelper("SimpleCL_CDSConfigModifyRequestEvent", "payload").createNewSubInstance("CDSConfigModifyRequestPayloadEntry");
    payloadEntry.put("hostname",  newHostname)
    payloadEntry.put("IP", executor.inFields.get("ipaddress-v4-oam"));
    payloadEntry.put("pnfName", otherFieldsMap.get("pnfName"));

    var payload = executor.subject.getOutFieldSchemaHelper("SimpleCL_CDSConfigModifyRequestEvent", "payload").createNewInstance();
    payload.put("update_DasH_config_DasH_request", payloadEntry);

    var cdsRequestEventFields = java.util.HashMap();
    cdsRequestEventFields.put("commonHeader", commonHeader);
    cdsRequestEventFields.put("actionIdentifiers", actionIdentifiers);
    cdsRequestEventFields.put("payload", payload);
    executor.addFieldsToOutput(cdsRequestEventFields);

    var logEventFields = populateLogEventFields(commonEventHeader.get("eventId"), otherFieldsMap.get("pnfName"), "PNF", "SUCCESS", logMessage)
    executor.addFieldsToOutput(logEventFields);
}
executor.logger.info(executor.outFieldsList);
returnVal;

