var timestamp = longType(new Date().getTime());
send(commonEventHeader.get("eventId"), "VNF", commonEventHeader.get("sourceId"), timestamp, "SUCCESS", "VNF details are received from AAI successfully. Sending ConfigModify request to CDS.")
true;

